import React, {Component} from 'react';
import './App.css';
import Result from './components/Result';
import KeyPad from './components/KeyPad';
import ResultHistory from './components/ResultHistory';

class App extends Component{
  constructor(){
    super();

    this.state = {
      result: "",
      history: []
    }
  }

  onClick = button => {
    if(button === "="){
      this.calculate()
    }else if(button === "C"){
      this.reset()
    }else if(button === "CE"){
      this.backspace()
    }else{
      this.setState({
        result: this.state.result + button
      })
    }
  }

  calculate = () => {
    try{
      //this.resulthistory();


      this.setState({
        //eslint-disbale-next-line
        result: (eval(this.state.result) || "") + ""
      })
    }catch(e){
      this.setState({
        result: "Error"
      })
    }
  }

  reset = () => {
    this.setState({
      result: ""
    })
  }

  backspace = () => {
    this.setState({
      result: this.state.result.slice(0, -1)
    })
  }

  resulthistory = () => {
    this.setState({
      history: this.state.history.concat(this.state.result + " = " + eval(this.state.result))
    })
  }

  render(){
    return(
      <div>
        <div className="calculator">
          <h1>Calculator with React JS</h1>
          <ResultHistory history={this.state.history}/>
          <Result result={this.state.result}/>
          <KeyPad onClick={this.onClick}/>
        </div>
      </div>
    )
  }
}

export default App;
